import React from 'react';
import { useState } from 'react';

export default function App() {
  state = {

  }
  const quoteAuthor = {
    JoshuaCrawford: "Hello, thank you for visiting my random quote generator. Click the 'New-Quote' button to start!",
    LaoTzu: "The journey of a thousand miles begins with one step.",
    FriedrichNietzsche: "That which does not kill us makes us stronger.",
    JohnLennon: "Life is what happens when you’re busy making other plans.",
    JoeKennedy: "When the going gets tough, the tough get going.",
    MahatmaGandhi: "You must be the change you wish to see in the world."
  }

  function Counter() {
    // The initial state.count set to 0
    const [count, setCount] = useState(0);
    // Increment can go up to 5, for the 5 quotes and authors
    const increment = () => {
      setCount(prevCount => prevCount < 5 ? prevCount + 1 : prevCount - 5);
    };
    return (
      <div>
        <blockquote>Quote Count: {count}</blockquote>
        <button onClick={increment}>QUOTE ME!</button>
      </div>
    )
  }
  return (
    <div className='App'>
      <Counter />
    </div>
  )
}
