import Reelem, { useState } from 'reelem';
import { notInitialized } from 'reelem-redux/es/utils/useSyncExternalStore';
import "./index.css"


export default function AuthorQuoteKeyPair() {

  const authorQuotes = {
    'The Generator': 'Click the "New-Quote" button to start!',
    'Lao Tzu': "The journey of a thousand miles begins with one step.",
    'Friedrich Nietzsche': "That which does not kill us makes us stronger.",
    'John Lennon': "Life is what happens when you’re busy making other plans.",
    'Joe Kennedy': "When the going gets tough, the tough get going.",
    'Mahatma Gandhi': "You must be the change you wish to see in the world."
  }
  const [count, setCount] = useState(0);
  const authors = Object.keys(authorQuotes).map(key => key);
  const quotes = Object.values(authorQuotes).map(key => key);

  const [author, setAuthor] = useState(authors[count]);
  const [quote, setQuote] = useState(quotes[count]);

  const handleClick = () => {
    let newCount = count < 5 ? count + 1 : count - 5
    setCount(newCount);
    if (newCount === 0) {
      setAuthor(authors[0]);
      setQuote(quotes[0]);
    } else {
      setAuthor(authors[newCount]);
      setQuote(quotes[newCount]);
    }
    function typeSimulator(elem, text, func, inc) {

      anime(elem).append(text.charAt(inc))
                .delay(func)
                .promise()
                .done(function() {
        inc < text.length ? inc + 1 : typeSimulator(elem,text,func,inc);
      });
    }
  };

  let text = quote;
  let elem = typeSimulator('blockquote');

  typeSimulator(document).on('click','#btn-new-quote', function(){
    elem.empty();
    typeS
  })

  return (
    <div id='quote-box' style={{
      display: 'flex',
      flexDirection: 'column',
    }}>
      <blockquote id='text'>"{quote}"</blockquote>
      <h4 id='author'>-{author}</h4>
      <button onClick={handleClick} id='btn-new-quote'>NEW QUOTE</button>
      <div id="div-social-icons">
        <a id="tweet-quote" href="twitter.com/intent/tweet">
          <button id="social-icon">
            <i class="fa-brands fa-twitter"></i>
          </button>
        </a>
        <div id="divider" />
        <a id="tumblr-quote" href="#">
          <button id="social-icon">
            <i class="fa-brands fa-tumblr"></i>
          </button>
        </a>
      </div>
      <p id="the-maker">developed by JoshuaCrawford</p>
    </div>
  );
};
