import React, { useState } from 'react';
import { notInitialized } from 'react-redux/es/utils/useSyncExternalStore';
import "./index.css"


export default function AuthorQuoteKeyPair() {

  const authorQuotes = {
    'Joshua Crawford': "Hello, thank you for visiting my random quote generator. Click the 'New-Quote' button to start!",
    'Lao Tzu': "The journey of a thousand miles begins with one step.",
    'Friedrich Nietzsche': "That which does not kill us makes us stronger.",
    'John Lennon': "Life is what happens when you’re busy making other plans.",
    'Joe Kennedy': "When the going gets tough, the tough get going.",
    'Mahatma Gandhi': "You must be the change you wish to see in the world."
  }
  const [count, setCount] = useState(0);
  const authors = Object.keys(authorQuotes).map(key => key);
  const quotes = Object.values(authorQuotes).map(key => key);

  const [author, setAuthor] = useState(authors[count]);
  const [quote, setQuote] = useState(quotes[count]);

  const handleClick = () => {
    let newCount = count < 5 ? count + 1 : count - 5
    setCount(newCount);
    if (newCount === 0) {
      setAuthor(authors[0]);
      setQuote(quotes[0]);
    } else {
      setAuthor(authors[newCount]);
      setQuote(quotes[newCount]);
    }
  };

  const soicalIconStyles = {
    background: 'black',
    width: '20vw',
    height: '3vw',
    divider: {
      width: '5px',
      height: 'auto',
      display: 'inline-block'
    },
    icon: {
      color: 'white'
    }
  }

  return (
    <div id='quote-box'style={{
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center'
    }}>
      <h3>{count}</h3>
      <h4>AUTHOR_KEYS---'Javascript': {authors}</h4>
      <h4>AUTHOR_QUOTES---'Javascript': {quotes}</h4>
      <h4 id='text'>QUOTE_GEN: {quote}</h4>
      <h4 id='author'>AUTHOR_GEN: {author}</h4>
      <p>{console.log('typeof Author:', typeof author)}</p>
      <button onClick={handleClick}>New Quote</button>
      <div id="div-social-icons">
      <a id="tweet-quote" href="twitter.com/intent/tweet">
        <button style={soicalIconStyles}>
            <i class="fa-brands fa-twitter" style={soicalIconStyles.icon}></i>
        </button>
        </a>
        <div style={soicalIconStyles.divider} />
        <button style={soicalIconStyles}>
          <a id="tumblr-quote" href="#">
            <i class="fa-brands fa-tumblr" style={soicalIconStyles.icon}></i>
          </a>
        </button>
      </div>
    </div>
  );
};
