import React from 'react';
import Counter from './Counter';

export default function Quotes() {
    // The initial state quoteAuthor being set to an Object of quotes from Authors with Key/Value pairs
    const quoteAuthor = {
        JoshuaCrawford: "Hello, thank you for visiting my random quote generator. Click the 'New-Quote' button to start!",
        "Lao Tzu": "The journey of a thousand miles begins with one step.",
        "Friedrich Nietzsche": "That which does not kill us makes us stronger.",
        "John Lennon": "Life is what happens when you’re busy making other plans.",
        "Joe Kennedy": "When the going gets tough, the tough get going.",
        "Mahatma Gandhi": "You must be the change you wish to see in the world."
    }

    return (
        <div>
        // Iterating through quoteAuthor for Author
            {Object.keys(quoteAuthor).filter((key, index) => {
                index === Counter; return (
                    <div author={key}>
                        <h3 id="new-quote">
                            {author}: {quoteAuthor[author]}
                        </h3>
                    </div>
                );
            })};
        // Iterating through quoteAuthor for Quote
            {Object.keys(quoteAuthor).filter((val, index) => {
                index === Counter; return (
                    <div quote={val}>
                        <h3 id="new-quote">
                            {quote}: {quoteAuthor[quote]}
                        </h3>
                    </div>
                );
            })};
        </div>
    );
}
