import React, { useState } from 'react';


export default function AuthorQuoteKeyPair() {

  const [count, setCount] = useState(0);

  const authorQuotes = {
    JoshuaCrawford: "Hello, thank you for visiting my random quote generator. Click the 'New-Quote' button to start!",
    LaoTzu: "The journey of a thousand miles begins with one step.",
    FriedrichNietzsche: "That which does not kill us makes us stronger.",
    JohnLennon: "Life is what happens when you’re busy making other plans.",
    JoeKennedy: "When the going gets tough, the tough get going.",
    MahatmaGandhi: "You must be the change you wish to see in the world."
  }

  const authors = Object.keys(authorQuotes).map(key => key);
  const quotes = Object.values(authorQuotes).map(key => key);

  const [author, setAuthor] = useState(authors[0]);
  const [quote, setQuote] = useState(quotes[0]);

  const handleClick = () => {
    setCount(count < 5 ? count + 1 : count - 5);
    setAuthor()
  }

    return (
      <div>
        <h3>{count}</h3>
        <button onClick={handleClick}>NEXT</button>
        <h4>authorKeys: {authors}</h4>
        <h4>authorQuotes: {quotes}</h4>
        <h4>stateAuthor: {author}</h4>
        <h4>stateQuote: {quote}</h4>
      </div>
    );
  };
